mapper.update:
UPDATE bbs        
SET ansnum = ansnum + 1
WHERE grpno = #{grpno}   
AND ansnum > #{ansnum}

UPDATE bbs           
SET viewcnt = viewcnt + 1
WHERE bbsno=#{bbsno}

mapper.select:
SELECT COUNT(*)
FROM bbs   
WHERE refnum=#{bbsno}

select count(*) from bbs
where bbsno = #{bbsno}
and passwd = #{passwd}

mapper.update:
UPDATE bbs                 
SET wname=#{wname}, title=#{title}, content=#{content}
WHERE bbsno = #{bbsno}

mapper.select:
SELECT bbsno, wname, title, content, viewcnt, wdate
FROM bbs
WHERE bbsno = #{bbsno}
